//1.导入vue 和 vuerouter 的包
import Vue from 'vue'
import VueRouter from 'vue-router'
import Login from "@/components/Login.vue";
import MyIdex from "@/components/MyIndex.vue";
import MyHome from "@/components/MyHome.vue";
import MyCompany from "@/components/MyCompany.vue";
import MyComaudit from "@/components/MyComaudit.vue";
import MyMain from "@/components/MyMain.vue";


//2.调用vue.use() 函数，把 VueRouter 安装为 Vue 的插件
//vue.use()函数的作用，就是来安装插件的
Vue.use(VueRouter)


const routes= [
    {
        path:'/',
        component:Login
    },
    {path: '/myIndex',component: MyIdex,
        children:[
            {
                path:'/MyHome',
                meta:{
                    title:'首页'
                },
                component:MyHome
            },
            {
                path:'/MyCompany',
                meta:{
                    title:'企业基本信息管理',
                },
                component:MyCompany
            },
            {
              path:'/MyComaudit',
                meta:{
                    title:'企业基本信息审核',
                },
                component:MyComaudit
            },
            {
                path:'/MyMain',
                meta:{
                    title:'学生基本信息管理',
                },
                component:MyMain
            },

        ]
    }

]


//3.创建路由的实例对象
const router = new VueRouter({
    mode:'history',
    routes
})


export default router;
