//1.导入vue 和 vuerouter 的包
import Vue from 'vue'
import VueRouter from 'vue-router'
import MyLogin from "@/components/MyLogin.vue";
import MyIdex from "@/components/MyIndex.vue";
import MyHome from "@/components/MyHome.vue";
import MyCompany from "@/components/MyCompany.vue";
//2.调用vue.use() 函数，把 VueRouter 安装为 Vue 的插件
//vue.use()函数的作用，就是来安装插件的
Vue.use(VueRouter)

const routes= [
    {path:'/',component:MyLogin},
    {path: '/myIndex',component: MyIdex,
        children:[
            {
                path:'/MyHome',
                meta:{
                    title:'首页'
                },
                component:MyHome
            },
            {
                path:'/MyCompany',
                meta:{
                    title:'企业基本信息管理',
                },
                component:MyCompany
            }
        ]
    }

]
//3.创建路由的实例对象
const router = new VueRouter({
    mode:'history',
    routes
})

export default router;
