      for (let i = 0; i < res.data.length; i++) {
        this.quartersList[i] = {
          quartersId: res.data[i].quartersId,
          quartersFirst: res.data[i].quartersFirst,
          quartersSecond: res.data[i].quartersSecond,
          quartersThird: res.data[i].quartersThird
