         * @type {boolean}
         */
        let flag = false;
        for (let j = 0; j < this.quartersFirst.length; j++) {
          if (this.quartersList[i].quartersFirst == this.quartersFirst[j].value) flag = true;
        }
        if (!flag) this.quartersFirst[this.quartersFirst.length] = {
          value: this.quartersList[i].quartersFirst
